import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardActionArea, CardContent, CardMedia, Grid, List, Table, TableBody, TableCell, TableRow, Typography } from '@material-ui/core';
import { Descriptions } from 'antd';
import SubscriptionPlanPreview from 'components/premium/SubscriptionPlanPreview';

const useStyles = makeStyles((theme) => ({
    layout: {
        display: 'flex',
        flex: 1,
        maxWidth: '100%',
    },
    root: {
        maxWidth: 345,
    },
    media: {
        height: 170,
    },
}));

const StepperInformation = ({ step, pet, petOwner, loggedInUser }) => {
    const classes = useStyles();

    const petColumns = [
        { title: 'Name', data: pet.officialName },
        { title: 'Sex', data: pet.sex },
        { title: 'Species', data: pet.species },
        { title: 'Breed', data: pet.breed },
    ];

    return step === 0 ? (
        <div className={classes.layout}>
            <Grid container direction="row" align="center" justify="center" alignItems="center">
                <Grid item xs={6}>
                    <Typography gutterBottom variant="h6">
                        Pet Information
                    </Typography>
                    <Card className={classes.root}>
                        <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                        <CardContent>
                            <Table>
                                {petColumns.map((item) => {
                                    return (
                                        <TableRow hover>
                                            <TableCell>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    {item.title}
                                                </Typography>
                                            </TableCell>
                                            <TableCell>
                                                <Typography>{item.data}</Typography>
                                            </TableCell>
                                        </TableRow>
                                    );
                                })}
                            </Table>
                        </CardContent>
                    </Card>
                </Grid>
                <Grid item xs={6}>
                    <Typography gutterBottom variant="h6">
                        User Information
                    </Typography>
                    <Card className={classes.root}>
                        <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                        <CardContent>
                            <Table>
                                {petColumns.map((item) => {
                                    return (
                                        <TableRow hover>
                                            <TableCell>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    {item.title}
                                                </Typography>
                                            </TableCell>
                                            <TableCell>
                                                <Typography>{item.data}</Typography>
                                            </TableCell>
                                        </TableRow>
                                    );
                                })}
                            </Table>
                        </CardContent>
                    </Card>
                </Grid>
            </Grid>
        </div>
    ) : step === 1 ? (
        <Grid item xs={12}>
            <Typography gutterBottom variant="h6">
                Payment Information
            </Typography>
            <Card className={classes.root}>
                <CardContent>
                    <SubscriptionPlanPreview user={loggedInUser} />
                </CardContent>
            </Card>
        </Grid>
    ) : null;
};

export default StepperInformation;
