import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardActionArea, CardContent, CardMedia, Grid, List, Table, TableBody, TableCell, TableRow, Typography } from '@material-ui/core';
import { Descriptions } from 'antd';
import SubscriptionPlanPreview from 'components/premium/SubscriptionPlanPreview';
import { PayPalScriptProvider, PayPalButtons } from '@paypal/react-paypal-js';
import PayPalPayment from './PayPalPayment';

const useStyles = makeStyles((theme) => ({
    layout: {
        display: 'flex',
        flex: 1,
        maxWidth: '100%',
    },
    root: {
        maxWidth: 345,
    },
    media: {
        height: 170,
    },
}));

const StepperInformation = ({ step, pet, petOwner, loggedInUser, setIsPayed }) => {
    const classes = useStyles();
    const isFreeOfCharge = loggedInUser.subscriptionPlan === 'premium' && pet.price === 0; // premium user don't need to pay any fees for the free pet

    const petColumns = [
        { title: 'Name', data: pet.officialName },
        { title: 'Sex', data: pet.sex },
        { title: 'Species', data: pet.species },
        { title: 'Breed', data: pet.breed },
    ];

    const calculatePrice = () => {
        if (loggedInUser.subscriptionPlan === 'free') {
            let fee = Math.min(pet.price * 0.05, 20); // 5% of the pet price or maximum 20€ as fee
            fee = Math.max(fee, 1); // min 1€
            return pet.price + fee;
        } else {
            return pet.price;
        }
    };

    return (
        <div className={classes.layout}>
            {step === 0 ? (
                // Infos for step 1: pet and pet owner short previews
                <Grid container direction="row" align="center" justify="center" alignItems="center">
                    <Grid item xs={6}>
                        <Typography gutterBottom variant="h6">
                            Pet Information
                        </Typography>
                        <Card className={classes.root}>
                            <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                            <CardContent>
                                <Table>
                                    {petColumns.map((item) => {
                                        return (
                                            <TableRow hover>
                                                <TableCell>
                                                    <Typography variant="body2" color="textSecondary" component="p">
                                                        {item.title}
                                                    </Typography>
                                                </TableCell>
                                                <TableCell>
                                                    <Typography>{item.data}</Typography>
                                                </TableCell>
                                            </TableRow>
                                        );
                                    })}
                                </Table>
                            </CardContent>
                        </Card>
                    </Grid>
                    <Grid item xs={6}>
                        <Typography gutterBottom variant="h6">
                            User Information
                        </Typography>
                        <Card className={classes.root}>
                            <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                            <CardContent>
                                <Table>
                                    {petColumns.map((item) => {
                                        return (
                                            <TableRow hover>
                                                <TableCell>
                                                    <Typography variant="body2" color="textSecondary" component="p">
                                                        {item.title}
                                                    </Typography>
                                                </TableCell>
                                                <TableCell>
                                                    <Typography>{item.data}</Typography>
                                                </TableCell>
                                            </TableRow>
                                        );
                                    })}
                                </Table>
                            </CardContent>
                        </Card>
                    </Grid>
                </Grid>
            ) : step === 1 ? (
                <Grid container direction="column" alignItems="center" justify="center" item xs={12} spacing={5}>
                    <Grid item align="center">
                        <Typography gutterBottom style={{ fontSize: 16 }}>
                            Your Current Subscription
                        </Typography>
                        <SubscriptionPlanPreview user={loggedInUser} />
                    </Grid>
                    <Grid item align="center" style={{ maxWidth: 500 }}>
                        {isFreeOfCharge ? (
                            () => setIsPayed(true)
                        ) : (
                            <>
                                <Typography>Pay now </Typography>
                                {/* Payment with PayPal Component */}
                                <PayPalPayment amount={calculatePrice()} setIsPayed={setIsPayed} />
                                {loggedInUser.subscriptionPlan === 'free' ? (
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        * We additionally charge 5% (min 1€ and max 20€) from the price for the transaction, because you have a free plan.
                                    </Typography>
                                ) : null}
                            </>
                        )}
                    </Grid>
                </Grid>
            ) : null}
        </div>
    );
};

export default StepperInformation;
