import React from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

/**
 * Small preview for the subscription plan
 * @param {props} props
 */
const SubscriptionPlanPreview = ({ user }) => {
    const classes = useStyles();

    return (
        <Grid item xs={12} className={classes.root}>
            {user.subscriptionPlan === 'premium' ? (
                <Card className={classes.premium}>
                    <CardContent>
                        <Typography gutterBottom variant="h6" className={classes.labelPremium}>
                            {user.subscriptionPlan}
                        </Typography>
                    </CardContent>
                </Card>
            ) : (
                <Card className={classes.free}>
                    <CardContent className={classes.card}>
                        <Typography variant="h6" className={classes.labelFree}>
                            Free
                        </Typography>
                    </CardContent>
                </Card>
            )}
        </Grid>
    );
};

const useStyles = makeStyles((theme) => ({
    premium: {
        minWidth: 255,
        transform: 'scale(1.05)',
        boxShadow: '0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)',
        border: `2px solid #3E4059`,
        ':hover&': {
            border: `2px solid #3E4059`,
            backgroundColor: 'rgba(62,64,89, 0.5)',
        },
    },
    free: {
        minWidth: 250,
        borderRadius: '4px',
        background: '#F3F1E0',
        boxShadow: '0 6px 10px rgba(0,0,0,.08), 0 0 6px rgba(0,0,0,.05)',
        transition: '.3s transform cubic-bezier(.155,1.105,.295,1.12),.3s box-shadow,.3s -webkit-transform cubic-bezier(.155,1.105,.295,1.12)',
        cursor: 'pointer',
        border: `1px solid #EAE8E8`,
        ':hover&': {
            transform: 'scale(1.05)',
            boxShadow: '0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)',
        },
    },
    root: {
        maxWidth: 345,
    },
    card: {
        display: 'flex',
        justifyContent: 'center',
    },
    labelPremium: {
        color: 'white',
        fontWeight: "lighter",
    },
    labelFree: {
        color: 'black',
        fontWeight: "lighter",
    },
}));

export default SubscriptionPlanPreview;
