import React, { useEffect, useRef, useState } from 'react';
import { Button, Chip, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, FormControl, InputLabel, makeStyles, MenuItem, Select } from '@material-ui/core';

const STATUS_TYPE = {
    PENDING: 'pending',
    SUCCESS: 'success',
    FAIL: 'fail',
};

const VARIANT_TYPE = {
    SENDER: 'sender',
    RECEIVER: 'receiver',
    DEFAULT: 'default',
};

const AdminStatusTag = (props) => {
    const { status, variant, confirm, transaction } = props;
    const ref = useRef(null);
    const selectorRef = useRef(null);

    const [tagStatus, setTagStatus] = useState(status);
    const [statusToChange, setStatusToChange] = useState(status);
    const [selectorOpened, setSelectorOpened] = useState(false);
    const [tagClicked, setTagClicked] = useState(false);
    const [open, setOpen] = useState(false);

    const color = () => {
        switch (status) {
            case STATUS_TYPE.PENDING:
                return { color: 'orange', background: 'white', borderColor: '#FDCD7F' };
            case STATUS_TYPE.SUCCESS:
                return { color: 'green', background: 'white', borderColor: '#A1CF6B' };
            case STATUS_TYPE.FAIL:
                return { color: 'red', background: 'white', borderColor: '#E87461' };
            default:
                return { color: 'orange', background: 'white', borderColor: 'orange' };
        }
    };

    useEffect(() => {
        /**
         * Alert if clicked on outside of element
         */
        function handleClickOutside(event) {
            if (ref.current && !ref.current.contains(event.target)) {
                // if (!selectorRef.current.contains(event.target)) setTagClicked(false);
                console.log('Clicked ref')
            }
            if (selectorRef.current && !selectorRef.current.contains(event.target)) {
                // if (!selectorRef.current.contains(event.target)) setTagClicked(false);
                console.log('Clicked selectorRef')
            }
        }

        // Bind the event listener
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            // Unbind the event listener on clean up
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [ref, open, selectorOpened]);

    const handleStatusChange = (event) => {
        const status = event.target.value;
        setOpen(true);
        setStatusToChange(status);
    };

    const handleClose = () => {
        setOpen(false);
        setTagClicked(false);
    };

    const handleConfirm = () => {
        let transactionCopy = transaction;
        switch (variant) {
            case VARIANT_TYPE.SENDER:
                transactionCopy.senderResponse = statusToChange;
                break;
            case VARIANT_TYPE.RECEIVER:
                transactionCopy.receiverResponse = statusToChange;
                break;
            case VARIANT_TYPE.DEFAULT:
                transactionCopy.status = statusToChange;
                break;
            default:
                transactionCopy.status = statusToChange;
                break;
        }

        confirm(transactionCopy);

        setTagStatus(statusToChange);
        setOpen(false);
        setTagClicked(false);
    };

    const openTag = () => {
        setSelectorOpened(true);
        setTagClicked(true);
    };

    return (
        <>
            {!tagClicked ? (
                <Chip variant="outlined" key={status} label={status.toUpperCase()} style={{ ...color(), borderWidth: 2, fontWeight: 'lighter' }} onClick={openTag} />
            ) : (
                <div ref={ref}>
                    <FormControl required variant="outlined" size="small" fullWidth>
                        <InputLabel id="response-label">Response</InputLabel>
                        <Select
                            ref={selectorRef}
                            label="Response"
                            id="response"
                            value={tagStatus}
                            onChange={handleStatusChange}
                            onBlur={handleStatusChange}
                        >
                            <MenuItem value={STATUS_TYPE.PENDING}>{STATUS_TYPE.PENDING}</MenuItem>
                            <MenuItem value={STATUS_TYPE.SUCCESS}>{STATUS_TYPE.SUCCESS}</MenuItem>
                            <MenuItem value={STATUS_TYPE.FAIL}>{STATUS_TYPE.FAIL}</MenuItem>
                        </Select>
                    </FormControl>
                </div>
            )}
            <Dialog open={open} onClose={handleClose} onBlur={handleClose} aria-labelledby="alert-dialog-title" aria-describedby="alert-dialog-description">
                <DialogTitle id="alert-dialog-title">{'Confirm Action'}</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">Please confirm you want to officially change the status.</DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleConfirm} color="primary" autoFocus>
                        Confirm
                    </Button>
                </DialogActions>
            </Dialog>
        </>
    );
};

const useStyles = makeStyles((theme) => ({}));

export default AdminStatusTag;
