import React from 'react';
import { ListItem, ListItemIcon, ListItemText, ListItemAvatar, Avatar, makeStyles } from '@material-ui/core';
// icons import
import GetAppIcon from '@material-ui/icons/GetApp';
import PetsIcon from '@material-ui/icons/Pets';
import PersonRoundedIcon from '@material-ui/icons/PersonRounded';
import VerificationIcon from '../../components/VerificationIcon';
import CancelIcon from '@material-ui/icons/Cancel';
import DateRangeRoundedIcon from '@material-ui/icons/DateRangeRounded';

function PetInformationListItem(props) {
    if (props.primary) {
        const urlForOwner = 'http://localhost:3000/user/'.concat(props.primary);
        return (
            <ListItem
                button={isButton(props.itemType)}
                component={isButton(props.itemType) ? 'a' : null}
                href={props.itemType === 'document' ? props.url : props.itemType === 'owner' ? urlForOwner : null}
            >
                <ListItemAvatar>
                    <Icon itemType={props.itemType} />
                </ListItemAvatar>
                <ListItemText primary={props.primary} secondary={props.secondary} />
                {props.itemType === 'document' ? props.declined ? <CancelIcon style={{ fill: 'red' }} /> : <VerificationIcon verified={props.verified} /> : null}
            </ListItem>
        );
    }
    return null;
}

function Icon(props) {
    const classes = useStyles();
    switch (props.itemType) {
        case 'owner':
            return (
                <Avatar className={classes.avatar}>
                    <PersonRoundedIcon className={classes.icon} />
                </Avatar>
            );
        case 'name':
            return (
                <Avatar className={classes.avatar}>
                    <PersonRoundedIcon className={classes.icon} />
                </Avatar>
            );
        case 'nickname':
            return (
                <Avatar className={classes.avatar}>
                    <PersonRoundedIcon className={classes.icon} />
                </Avatar>
            );
        case 'document':
            return (
                <Avatar className={classes.avatar}>
                    <GetAppIcon  className={classes.icon} />
                </Avatar>
            );
        default:
            return (
                <ListItemAvatar>
                    <Avatar className={classes.avatar}>
                        <PetsIcon className={classes.icon} />
                    </Avatar>
                </ListItemAvatar>
            );
    }
}

function isButton(type) {
    return type === 'owner' || type === 'document';
}

const useStyles = makeStyles((theme) => ({
    avatar: {
        background: theme.palette.secondary.main,
    },
    icon: {
        color: theme.palette.secondary.light,
    },
}));

export default PetInformationListItem;
