import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import { withRouter } from 'react-router-dom';
import { declineDocument, getDocuments, verifyDocument } from 'redux/actions/documentActions';
import TransactionsOverviewTable from 'components/transactions/TransactionsOverviewTable';
import { getAdminTransactions, getTransactions } from 'redux/actions';
import { useUser } from 'helper/hooks/auth.hooks';
import TransactionsAdminOverviewTable from 'components/transactions/TransactionsAdminOverviewTable';
import DocumentListView from './DocumentListView';
import { Typography } from '@material-ui/core';

const AdminConsoleView = (props) => {
    const classes = useStyles();
    const dispatch = useDispatch();
    const user = useUser();
    const transactions = useSelector((state) => state.transaction.transactions);

    useEffect(() => {
        dispatch(getAdminTransactions(user.id));
    }, [transactions.length, user.id, dispatch]);

    return (
        <div className={classes.layout}>
            <div>
                <Typography variant="h6" align="left">
                    Documents to verify
                </Typography>
                <DocumentListView />
            </div>
            <div className={classes.tableLayout}>
                <Typography variant="h6" align="left">
                    Manage transactions
                </Typography>
                <TransactionsAdminOverviewTable transactions={transactions} />
            </div>
        </div>
    );
};

const useStyles = makeStyles((theme) => ({
    tableLayout: {
        width: '90%',
        marginTop: 100,
    },
    layout: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
    },
}));

export default connect()(withRouter(AdminConsoleView));
