import React, { useState } from 'react';
import { Button, Modal } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import PaymentStepper from './transactions/PaymentStepper';

const useStyles = makeStyles((theme) => ({
    button: {
        margin: theme.spacing(3),
        padding: theme.spacing(1),
        paddingLeft: theme.spacing(6),
        paddingRight: theme.spacing(6),
    },
}));

function PaymentButton(props) {
    const classes = useStyles();
    const price = props.price;

    const [isStepperOpened, setIsStepperOpened] = useState(false);

    const openStepper = () => setIsStepperOpened(true);
    //TODO: The button needs to have payment funcitonality
    return (
        <>
            <Button variant="contained" color="secondary" className={classes.button} endIcon={<ShoppingCartIcon />} onClick={openStepper}>
                {price === 0 ? 'Free' : `${price} €`}
            </Button>
            {isStepperOpened ? <Modal><PaymentStepper /></Modal> : null}
        </>
    );
}

export default PaymentButton;
