import React, { useEffect } from 'react';
import { connect, useSelector } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import { withRouter } from 'react-router-dom';
import { declineDocument, getDocuments, verifyDocument } from 'redux/actions/documentActions';
import TransactionsOverviewTable from 'components/transactions/TransactionsOverviewTable';
import { getTransactions } from 'redux/actions';


const DocumentListView = (props) => {
    const classes = useStyles();
    const transactions = useSelector((state) => state.transactions.transactions);

    useEffect(() => {
        // load docs when the page is loaded or the docs were verified/declined.
        props.dispatch(getDocuments());
        props.dispatch(getTransactions())
    }, [props]);

    return (
        <div>
            <DocumentListView />
            <TransactionsOverviewTable transactions={transactions} />
        </div>
    );
};

const useStyles = makeStyles((theme) => ({
    filters: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'baseline',
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    button: {
        margin: theme.spacing(3),
    },
    ageSlider: {
        margin: theme.spacing(4),
        width: 200,
    },
}));

export default connect()(withRouter(DocumentListView));
