import React from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

/**
 * Small preview for the subscription plan
 * @param {props} props
 */
const SubscriptionPlanPreview = ({ user }) => {
    const classes = useStyles();

    return (
        <Grid item xs={6} className={classes.root}>
            <Card className={user.subscriptionPlan === 'premium' ? classes.premium : classes.free}>
                <CardContent>
                    <Typography gutterBottom variant="h6" className={classes.label}>
                        {user.subscriptionPlan}
                    </Typography>
                </CardContent>
            </Card>
        </Grid>
    );
};

const useStyles = makeStyles((theme) => ({
    premium: {
        minWidth: 275,
        transform: 'scale(1.05)',
        boxShadow: '0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)',
        border: `2px solid #3E4059`,
        ':hover&': {
            border: `2px solid #3E4059`,
            backgroundColor: 'rgba(62,64,89, 0.5)',
        },
    },
    free: {
        minWidth: 275,
        border: `2px solid #3E4059`,
        backgroundColor: '#3E4059',
        ':hover&': {
            backgroundColor: '#2B2C3E',
        },
    },
    root: {
        maxWidth: 345,
    },
    label: {
        color: 'white',
    }
}));

export default SubscriptionPlanPreview;
