import React from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

/**
 * Small preview for the subscription plan
 * @param {props} props
 */
const SubscriptionPlanPreview = ({ user }) => {
    const classes = useStyles();

    return (
        <Grid item xs={6} className={classes.root}>
            <Card className={user.subscriptionPlan === 'premium' ? classes.premium : classes.free}>
                <CardContent>{user.subscriptionPlan}</CardContent>
            </Card>
        </Grid>
    );
};

const useStyles = makeStyles((theme) => ({
    premium: {
        minWidth: 275,
        border: `2px solid #3E4059`,
        ':hover&': {
            border: `2px solid #3E4059`,
            backgroundColor: 'rgba(62,64,89, 0.5)',
        },
    },
    free: {
        minWidth: 275,
        border: `2px solid #3E4059`,
        backgroundColor: '3E4059',
        ':hover&': {
            border: `4px solid #3E4059`,
            backgroundColor: '#2B2C3E',
        },
    },
    root: {
        maxWidth: 345,
    },
}));

export default SubscriptionPlanPreview;
