import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardActionArea, CardContent, CardMedia, Grid, List, Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    layout: {
        display: 'flex',
        flex: 1,
        maxWidth: '100%',
    },
    root: {
        maxWidth: 345,
    },
    media: {
        height: 140,
    },
}));

const StepperInformation = ({step, pet, user}) => {
    const classes = useStyles();

    return (
        <div className={classes.layout}>
            <Grid container direction="row" align="center" justify="center" alignItems="center">
                <Grid item xs={6}>
                    <Card className={classes.root}>
                            <CardMedia className={classes.media} image={pet.profilePicture} title="pet profile picture" />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    Lizard
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica
                                </Typography>
                            </CardContent>
                    </Card>
                </Grid>
                <Grid item xs={6}></Grid>
            </Grid>
        </div>
    );
};

export default StepperInformation;
