import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardActionArea, CardContent, CardMedia, Grid, List, Typography } from '@material-ui/core';
import { Descriptions, Table } from 'antd';

const useStyles = makeStyles((theme) => ({
    layout: {
        display: 'flex',
        flex: 1,
        maxWidth: '100%',
    },
    root: {
        maxWidth: 345,
    },
    media: {
        height: 170,
    },
}));

const StepperInformation = ({ step, pet, user }) => {
    const classes = useStyles();

    const petColumns = [
        { title: 'Name', dataIndex: 'officialName' },
        { title: 'Sex', dataIndex: 'sex' },
        { title: 'Breed', dataIndex: 'breed' },
        { title: 'Species', dataIndex: 'species' },
    ];

    return (
        <div className={classes.layout}>
            <Grid container direction="row" align="center" justify="center" alignItems="center">
                <Grid item xs={6}>
                    <Typography gutterBottom variant="h6">
                        Pet Information
                    </Typography>
                    <Card className={classes.root}>
                        <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                        <CardContent>
                            <Table columns={[]} dataSource={[pet]} />
                        </CardContent>
                    </Card>
                </Grid>
                <Grid item xs={6}>
                    <Typography gutterBottom variant="h6">
                        User Information
                    </Typography>
                    <Card className={classes.root}>
                        <CardMedia className={classes.media} image={pet.profilePicture.src} title="pet profile picture" />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                Lizard
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>
            </Grid>
        </div>
    );
};

export default StepperInformation;
